import { createContext } from 'react';

const Context = createContext({
  cars: {
    red: false,
    blue: false,
    yellow: false,
  },
  moveCar: function moveCar(car, side) {
    this.setState({
      cars: {
        ...this.state.cars,
        [car]: side,
      },
    });
  },
});

export default Context;
