import React from 'react';
import carBlue from './images/carBlue.jpeg';
import carRed from './images/carRed.jpeg';
import carYellow from './images/carYellow.jpeg';
import Context from './context/Context';

function Cars({ redCar, blueCar, yellowCar }) {
  return (
    <Context.Consumer>
      {(value) => (
        <div>
          <div>
            <img
              className={value.cars.red ? "car-right" : "car-left"}
              src={carRed}
              alt="red car"
            />
            <button onClick={() => value.moveCar("red", !redCar)} type="button">
              Move
            </button>
          </div>
          <div>
            <img
              className={blueCar ? "car-right" : "car-left"}
              src={carBlue}
              alt="blue car"
            />
            <button onClick={() => value.moveCar("blue", !blueCar)} type="button">
              Move
            </button>
          </div>
          <div>
            <img
              className={yellowCar ? "car-right" : "car-left"}
              src={carYellow}
              alt="yellow car"
            />
            <button onClick={() => value.moveCar("yellow", !yellowCar)} type="button">
              Move
            </button>
          </div>
        </div>
      )}
    </Context.Consumer>
  );
}

export default Cars;
